<?php

declare(strict_types=1);

namespace MiniOrange\OAuth\Model;

use Magento\Framework\ObjectManagerInterface;

/**
 * Factory class for MiniorangeOauthClientApps model.
 *
 * In a production Magento installation this class is auto-generated by the DI
 * framework.  This hand-written version exists so that:
 *  - PHPUnit can create mocks of it in standalone tests (no Magento required)
 *  - Static-analysis tools (PHPStan/Psalm) can resolve the type without stubs
 *
 * The real generated factory has exactly the same public API.
 */
class MiniorangeOauthClientAppsFactory
{
    /** @var ObjectManagerInterface */
    private readonly ObjectManagerInterface $objectManager;

    /**
     * @param ObjectManagerInterface $objectManager
     */
    public function __construct(ObjectManagerInterface $objectManager)
    {
        $this->objectManager = $objectManager;
    }

    /**
     * Create a new MiniorangeOauthClientApps instance.
     *
     * @param  array<string,mixed> $data
     */
    public function create(array $data = []): MiniorangeOauthClientApps
    {
        return $this->objectManager->create(MiniorangeOauthClientApps::class, $data);
    }
}
